import React, {Component} from 'react';
import './form.css';

class form extends Component {
  constructor(props) {
    super();
    this.heading = props.heading;
  }

render() {
  if(this.state.redirect) {
      return <redirect to ={"/darkKnight?info=" + this.state.infoShow}/>;
    }
  return (
    <form id="survey-form">
          <h1> {this.heading} </h1>

           <h2>How would you Rate this Movie?</h2>
        <label>
          <input name="user-recommend" type="radio" class="input-radio"/>Awesome!</label>
        <label>
          <input name="user-recommend" type="radio" class="input-radio" />Good.</label>
        <label><input name="user-recommend" type="radio" class="input-radio" />Could be better...</label>
        <label><input name="user-recommend" type="radio" class="input-radio" />TERRIBLE!</label>

      <button type="submit" id="submit" class="submit-button">
          Submit
        </button>

     </form>
);
}
}

export default form;
